function App() {
  const [data, setData] = useState()
  const [prew, setprew] = useState()

  const handelChange=(e) =>{
    const img = e.target.file[0]
    setData(img)
    const image = URL.CreayeObjectURL(img)
    setprew(image)



  }
  return (
    <div className="App">
      <input type='file'  onChnange={(e) => handelChange(e) }/>
     <img src={prew} alt={data} style={{width: '500px'}} /> 

    </div>
  );
}

export default App;
