import {Button, TextField, Card} from '@mui/material'
import React, {useState} from 'react'
import './Todo.css'
const Todo = () =>{

  const [inputVal, setInputVal] = useState('')
  const [like, setLike] = useState([0])
  const [items, setItems ] = useState([])
  const handelChange = (e) =>{
    setInputVal(e.target.value)
  }
  const handelAdd = () =>{
    setItems([...items, inputVal])
  }
  const handeleRemove = (id) =>{
    const newItem = items.filter((elem, ind) =>{
      return ind !== id
    })
    setItems(newItem)
  }
  const handeleLike = (id) =>{
    like.map((curr, ind) =>{
      alert(curr.like(id))
    })
}
  return(
    <Card variant='outlined'>
      <h1> Todo </h1>
      <TextField onChange={handelChange} label="enter something" variant="standard" />
      <Button onClick={handelAdd} variant='contained'> + </Button>
      {
        items.map((elem, ind) =>{
          return(
            <>
            <h1 key={ind}> {elem}</h1>
            <Button variant='outlined' onClick={() => handeleRemove(ind)}> remove </Button>
            <Button variant='outlined' onClick={() => handeleLike(ind)}> {like}</Button>
            </>
          )
        })
      }
    </Card>
  )
}
export default Todo
