import {Button, TextField, Card} from '@mui/material'
import React, {useState} from 'react'
import './Todo.css'
const Todo = () =>{
    const [userName, setUserName] = useState()
  const [message, setMessage] = useState()
  const [input, setInput] = useState([])
  const click = () =>{
    if (!userName && !message) {
      alert('fill the some data')
    }else{
      setInput([...input, {userName, message}])
      setMessage('')
      setUserName('')
    }
  }
  return(
    <>
    <Card variant='filled' className='form'>
      <h1> Add Your Note </h1>
      <TextField label='Enter your title' type='text' variant='outlined' onChange={e => setUserName(e.target.value)} value={userName} />
      <TextField label='enter mesaage' type='text' variant='outlined' onChange={e => setMessage(e.target.value)} value={message} />
      <Button className='Button' onClick={click} variant='outlined'> + </Button>
    </Card>
    <div className='flex'>
      {
        input.map((curr) =>{
          return (
            <>
            <Card className='filled'>
              <div className='cardTitle'>
                <h1 className='title'> {curr.userName} </h1>
                <hr className='hr' />
              </div>
              <div className='cardBody'>
                <h1 className='message'> {curr.message} </h1>
              <button variant='outlined' className='remove'> + </button>
              </div>
            </Card>
          </>
          )
        })
      }
      </div>
    </>
  )
}
export default Todo
