import {Button, TextField, Card} from '@mui/material'
import React, {useState} from 'react'
import './Todo.css'
const Todo = () =>{
    const [userName, setUserName] = useState()
  const [message, setMessage] = useState()
  const [input, setInput] = useState([])
  const click = () =>{
    if (!userName && !message) {
      alert('fill the some data')
    }else{
      setInput([...input, {userName, message}])
      setMessage('')
      setUserName('')
    }
  }
  return(
    <>
    <Card variant='filled'>
      <h1> Add Your Note </h1>
      <TextField label='Enter your title' type='text' variant='outlined' onChange={e => setUserName(e.target.value)} value={userName} />
      <TextField label='entwr mesaage' type='text' variant='outlined' onChange={e => setMessage(e.target.value)} value={message} />
      <button onClick={click}> + </button>
    </Card>
      {
        input.map((curr) =>{
          return (
            <>
            <Card className='filled'>
              <div className='cardTitle'>
                <h1 className='title'> {curr.userName} </h1>
              </div>
              <div className='cardBody'>
                <h1> {curr.message} </h1>
              </div>
            </Card>
          </>
          )
        })
      }
    </>
  )
}
export default Todo
