//import Todo from './Components/Todo'
import {useState} from 'react'
function App() {
  const [data, setData] = useState('')
  const [prew, setprew] = useState('')
  const handelChange=(e) =>{
    const newData = e.target.file[0]
    setData(newData)
    const slected = URL.createobjecturl(newData)
    setprew(slected)
    alert(slected)
    alert(newData)
  }
  return (
    <div className="App">
      <input type='file'  onChange={(e) => handelChange(e) }/>
     { <img src={prew} alt='gdh' style={{width: '500px'}} /> }

    </div>
  );
}

export default App;
