import {Button, TextField, Card} from '@mui/material'
import React, {useState, useEffect} from 'react'
import './Todo.css'
const Todo = () =>{
  const [userName, setUserName] = useState()
  const [message, setMessage] = useState()
  const [input, setInput] = useState([])
  const [toggle, setToggle] = useState(true)
  const [count, setCount] = useState(0)
  const time = new Date().toLocaleDateString()
  const click = () =>{
     if (!userName && !message) {
      alert('fill the some data')
    }else{
      setInput([...input, {userName, message}])
      setMessage('')
      setUserName('')
      setCount(count+1)
    }
  }
  const removeItem = (id) =>{
    const newData = input.filter((curr, ind) =>{
      return ind !== id
    })
    setInput(newData)
    setCount(count-1)
  }
  const editItem = (id) =>{
    //alert(id)
    const editedItem = input.find((curr, ind) =>{
       if (ind == id) {
          return(
             setUserName(curr.userName),
             setMessage(curr.message),
             setToggle(false)
          )
       }
    })
  }
  const saveEdit = () =>{
    alert('hii')
  }

  useEffect(() =>{

  }, [saveEdit])
  return(
    <>
    <Card variant='filled' className='form'>
      <h1> Add Your Note </h1>
      <TextField label='Enter your title' type='text' variant='outlined' onChange={e => setUserName(e.target.value)} value={userName} />
      <TextField label='enter mesaage' type='text' variant='outlined' onChange={e => setMessage(e.target.value)} value={message} />
      <Button className='Button' onClick={toggle ? click : saveEdit} variant='outlined'> Add Note </Button> 
      <h1> you have {count} notes </h1>
    </Card>
    <div className='flex'>
      {
        input.map((curr, ind) =>{
          return (
            <>
            <Card className='filled' key={ind}>
              <div className='cardTitle'>
                <h1 className='title'> {curr.userName} </h1>
                <hr className='hr' />
              </div>
              <div className='cardBody'>
                <h1 className='message'> {curr.message} </h1>
                <h1 className='message'> data was saved in {time} </h1>
              </div>
              <Button variant='outlined' className='remove' onClick={()=> removeItem(ind)}> Remove item</Button>
              <Button variant='outlined' className='remove' onClick={()=> editItem(ind)}> edit </Button>
            </Card>
          </>
          )
        })
      }
      </div>
    </>
  )
}
export default Todo
